export const mountPage = ({ compRef, page, layout, }) => {
    let pageRef = null;
    let layoutRef = null;
    if (!layout) {
        pageRef = compRef;
    }
    else {
        layoutRef = compRef;
        pageRef = layoutRef.instance.page.createComponent(page);
    }
    return {
        pageRef,
        layoutRef,
    };
};
